(null, 85, 'So Product Label', 'so-product-label', 'OpenCartWorks', '1.1.0', 'http://www.opencartworks.com/', '<?xml version="1.0" encoding="UTF-8"?>\r\n<modification>\r\n    <name>So Product Label</name>\r\n    <code>so-product-label</code>\r\n    <version>1.1.0</version>\r\n    <author>OpenCartWorks</author>\r\n    <link>http://www.opencartworks.com/</link>\r\n\r\n    <file path="admin/view/template/catalog/product_form.twig">\r\n        <operation>\r\n            <search><![CDATA[<li><a href="#tab-design" data-toggle="tab">{{ tab_design }}</a></li>]]></search>\r\n            <add position="after"><![CDATA[<li><a href="#tab-productlabel" data-toggle="tab">{{ tab_productlabel }}</a></li>]]></add>\r\n        </operation>\r\n\r\n        <operation>\r\n            <search><![CDATA[<div class="tab-pane" id="tab-design">]]></search>\r\n            <add position="before"><![CDATA[\r\n                <div class="tab-pane" id="tab-productlabel">\r\n                    <div class="accordion" id="so_accordion">\r\n                        {% for i in 0..8 %}\r\n                        {% if i==0 %}\r\n                            {% set ps=''Top-Left'' %}\r\n                        {% elseif i==1 %}\r\n                            {% set ps=''Top-Center'' %}\r\n                        {% elseif i==2 %}\r\n                            {% set ps=''Top-Right'' %}\r\n                        {% elseif i==3 %}\r\n                            {% set ps=''Middle-Left'' %}\r\n                        {% elseif i==4 %}\r\n                            {% set ps=''Middle-Center'' %}\r\n                        {% elseif i==5 %}\r\n                            {% set ps=''Middle-Right'' %}\r\n                        {% elseif i==6 %}\r\n                            {% set ps=''Bottom-Left'' %}\r\n                        {% elseif i==7 %}\r\n                            {% set ps=''Bottom-Center'' %}\r\n                        {% elseif i==8 %}\r\n                            {% set ps=''Bottom-Right'' %}\r\n                        {% endif %}\r\n                        <div class="accordion-group">\r\n                            <div class="accordion-heading">\r\n                                <h3><a class="accordion-toggle" data-toggle="collapse" data-parent="#so_accordion" href="#so_collapse{{ i }}">Position: {{ ps }}</a></h3>\r\n                            </div>\r\n                            <div id="so_collapse{{ i }}" class="accordion-body collapse">\r\n                                <div class="accordion-inner">\r\n                                    <div class="form-group">\r\n                                        <label class="col-sm-2 control-label" for="input-position">{{ entry_position }}</label>\r\n                                        <div class="col-sm-10">\r\n                                            <select name="product_label[{{ i }}][position]" class="form-control">\r\n                                                {% if i==0 %}\r\n                                                    <option value="topleft" {{ product_labels[i][''position''] is defined and product_labels[i][''position''] == ''topleft'' ? ''selected="selected"'' : '''' }}>Top Left</option>\r\n                                                {% elseif i==1 %}\r\n                                                    <option value="topcenter" {{ product_labels[i][''position''] is defined and product_labels[i][''position''] == ''topcenter'' ? ''selected="selected"'' : '''' }}>Top Center</option>\r\n                                                {% elseif i==2 %}\r\n                                                    <option value="topright" {{ product_labels[i][''position''] is defined and product_labels[i][''position''] == ''topright'' ? ''selected="selected"'' : '''' }}>Top Right</option>\r\n                                                {% elseif i==3 %}\r\n                                                    <option value="middleleft" {{ product_labels[i][''position'']